    19→
    20→class Track: ObservableObject, Identifiable, Codable {
    21→    let id: Int
    22→    @Published var waveformType: WaveformType = .sine
    23→    @Published var waveformData: [Float] = []
    24→    @Published var frequency: Float = 440.0
    25→    @Published var volume: Float = 0.5
    26→    @Published var isPlaying: Bool = false
    27→    @Published var portamentoTime: Float = 0.0  // 0-1000ms range
    28→
    29→    init(id: Int) {
    30→        self.id = id
    31→        generateDefaultWaveform()
    32→    }
    33→
    34→    enum CodingKeys: String, CodingKey {
    35→        case id, waveformType, waveformData, frequency, volume, isPlaying, portamentoTime
    36→    }
    37→
    38→    required init(from decoder: Decoder) throws {
    39→        let container = try decoder.container(keyedBy: CodingKeys.self)
    40→        id = try container.decode(Int.self, forKey: .id)
    41→        waveformType = try container.decode(WaveformType.self, forKey: .waveformType)
    42→        waveformData = try container.decode([Float].self, forKey: .waveformData)
    43→        frequency = try container.decode(Float.self, forKey: .frequency)
    44→        volume = try container.decode(Float.self, forKey: .volume)
    45→        isPlaying = try container.decode(Bool.self, forKey: .isPlaying)
    46→        portamentoTime = try container.decode(Float.self, forKey: .portamentoTime)
    47→    }
    48→
    49→    func encode(to encoder: Encoder) throws {
    50→        var container = encoder.container(keyedBy: CodingKeys.self)
    51→        try container.encode(id, forKey: .id)
    52→        try container.encode(waveformType, forKey: .waveformType)
    53→        try container.encode(waveformData, forKey: .waveformData)
