struct LiquidglassSliderStyle: ViewModifier {
    func body(content: Content) -> some View {
        content
            .tint(Color(red: 0.2, green: 0.9, blue: 0.3))
            .padding(.horizontal, 12)
            .padding(.vertical, 6)
            .background(
                ZStack {
                    // Inner shadow effect
                    RoundedRectangle(cornerRadius: 12)
                        .fill(
                            LinearGradient(
                                gradient: Gradient(colors: [
