import SwiftUI

/// アプリ全体のテーマカラーを管理する構造体
struct AppTheme {

    /// カラー定義
    struct Colors {

        // MARK: - 基本色

        /// メインの明るい緑（オシロスコープの蛍光色）
        /// 使用箇所: 波形の明るい部分、Stopボタン、アクティブな要素
        static let brightGreen = Color(red: 0.2, green: 1.0, blue: 0.3)

        /// 中間の緑
        /// 使用箇所: スライダーのティント、通常のボタン背景
        static let mediumGreen = Color(red: 0.1, green: 0.7, blue: 0.2)

        /// 暗い緑
        /// 使用箇所: 波形の暗い部分、Playボタン（停止中）
        static let darkGreen = Color(red: 0.0, green: 0.8, blue: 0.2)

        /// とても暗い緑
        /// 使用箇所: 非アクティブな要素、影
        static let veryDarkGreen = Color(red: 0.05, green: 0.3, blue: 0.1)

        /// 最も暗い緑
        /// 使用箇所: Playボタンの暗い部分
        static let darkestGreen = Color(red: 0.02, green: 0.25, blue: 0.05)

        // MARK: - 背景色

        /// メイン背景のグラデーション開始色
        /// 使用箇所: ContentViewの背景（左上）
        static let backgroundStart = Color(red: 0.1, green: 0.1, blue: 0.1)

        /// メイン背景のグラデーション終了色
        /// 使用箇所: ContentViewの背景（右下）
        static let backgroundEnd = Color(red: 0.05, green: 0.05, blue: 0.05)

        // MARK: - 波形表示

        struct Waveform {
            /// 波形のグラデーション（明るい緑から暗い緑）
            /// 使用箇所: WaveformDisplayView、MiniWaveformView
            static let gradient = [brightGreen, darkGreen]

            /// 波形の影の色
            /// 使用箇所: WaveformDisplayViewのグロー効果
            static let glow = Color(red: 0.0, green: 1.0, blue: 0.2).opacity(0.6)

            /// グリッドの色（とても暗い緑）
            /// 使用箇所: WaveformDisplayViewの10x8グリッド
            static let grid = Color.green.opacity(0.15)

            /// ミニ波形の背景色
            /// 使用箇所: MiniWaveformView（非再生時）
            static let miniBackground = Color.green.opacity(0.2)

            /// ミニ波形のアクティブ色
            /// 使用箇所: MiniWaveformView（再生時）
            static let miniActive = Color.green.opacity(0.1)
        }

        // MARK: - ボタン

        struct Button {
            /// Playボタン（停止中）の背景グラデーション
            /// 使用箇所: PlayStopButtonStyle（isStop = false）
            static let playBackgroundGradient = [veryDarkGreen, darkestGreen]

            /// Stopボタン（再生中）の背景グラデーション
            /// 使用箇所: PlayStopButtonStyle（isStop = true）
            static let stopBackgroundGradient = [mediumGreen,
                                                  Color(red: 0.05, green: 0.6, blue: 0.1)]

            /// 通常のボタン背景グラデーション
            /// 使用箇所: LiquidglassButtonStyle
            static let normalBackgroundGradient = [Color(red: 0.1, green: 0.4, blue: 0.2),
                                                    veryDarkGreen]

            /// Playボタンのボーダー
            /// 使用箇所: PlayStopButtonStyleのストローク（停止中）
            static let playBorder = [Color.green.opacity(0.5),
                                     Color.green.opacity(0.2)]

            /// Stopボタンのボーダー
            /// 使用箇所: PlayStopButtonStyleのストローク（再生中）
            static let stopBorder = [Color.green.opacity(0.7),
                                     Color.green.opacity(0.3)]

            /// Playボタンの影
            /// 使用箇所: PlayStopButtonStyleのシャドウ（停止中）
            static let playShadow = Color(red: 0.0, green: 0.3, blue: 0.05, opacity: 0.3)

            /// Stopボタンの影
            /// 使用箇所: PlayStopButtonStyleのシャドウ（再生中）
            static let stopShadow = Color(red: 0.0, green: 0.5, blue: 0.1, opacity: 0.3)
        }

        // MARK: - スライダー

        struct Slider {
            /// スライダーのティント色
            /// 使用箇所: LiquidglassSliderStyle
            static let tint = Color(red: 0.2, green: 0.9, blue: 0.3)
        }

        // MARK: - トラックタブ

        struct TrackTab {
            /// 再生中のインジケーター
            /// 使用箇所: TrackTabButtonの緑の点
