func generateDefaultWaveform() {
    waveformData = Array(repeating: 0, count: 512)
    switch waveformType {
    case .sine:
        for i in 0..<512 {
            let angle = Float(i) / 512.0 * Float.pi * 2
            waveformData[i] = sin(angle)
        }
    case .triangle:
        for i in 0..<512 {
            let phase = Float(i) / 512.0
            if phase < 0.25 {
                waveformData[i] = phase * 4
            } else if phase < 0.75 {
                waveformData[i] = 2 - phase * 4
            } else {
                waveformData[i] = phase * 4 - 4
            }
        }
    case .square:
        for i in 0..<512 {
            waveformData[i] = i < 256 ? 1.0 : -1.0
        }
    case .custom:
        break
    }
}

