                ControlPanelView(track: currentTrack)

                Spacer()

                TrackTabBar(selectedTrack: $selectedTrack)
                    .padding(.bottom, 10)
            }
            .padding(.horizontal, 20)
            .padding(.top, 10)
        }
    }
}

