struct MiniWaveformView: View {
    let waveformData: [Float]
    let isPlaying: Bool
    @State private var animationPhase: Double = 0

    var body: some View {
        GeometryReader { geometry in
            ZStack {
                // 背景の波形（静的）
                WaveformPath(data: downsampledData)
                    .stroke(
                        LinearGradient(
                            colors: [.gray.opacity(0.3), .gray.opacity(0.2)],
                            startPoint: .leading,
                            endPoint: .trailing
                        ),
                        lineWidth: 1
                    )

                // アニメーション波形（発音中のみ）
                if isPlaying {
                    WaveformPath(data: downsampledData)
                        .stroke(
                            LinearGradient(
                                colors: [.cyan, .blue],
                                startPoint: .leading,
                                endPoint: .trailing
                            ),
                            lineWidth: 1.5
                        )
                        .opacity(0.5 + sin(animationPhase) * 0.5) // パルスエフェクト
                        .scaleEffect(1.0 + sin(animationPhase) * 0.05) // 微妙な拡大縮小
                }
            }
        }
        .frame(height: 20)
        .onAppear {
            if isPlaying {
                withAnimation(.easeInOut(duration: 1.0).repeatForever(autoreverses: false)) {
                    animationPhase = .pi * 2
                }
            }
        }
        .onChange(of: isPlaying) { newValue in
            if newValue {
                withAnimation(.easeInOut(duration: 1.0).repeatForever(autoreverses: false)) {
                    animationPhase = .pi * 2
                }
            } else {
                withAnimation(.easeOut(duration: 0.3)) {
                    animationPhase = 0
                }
            }
        }
    }

    // 512点から32点にダウンサンプリング
