            let x = CGFloat(i) / 511.0 * effectiveWidth + padding
            let closestPoint = currentDrawingPoints.min(by: { abs($0.x - x) < abs($1.x - x) })

            if let point = closestPoint {
                let normalizedY = Float((point.y - padding) / effectiveHeight)
                let waveValue = 1.0 - normalizedY * 2.0
                newWaveform[i] = max(-1.0, min(1.0, waveValue))
            }
        }

        track.waveformData = newWaveform
    }
}

struct WaveformShape: Shape {
    let waveformData: [Float]

    func path(in rect: CGRect) -> Path {
        var path = Path()

        guard waveformData.count > 0 else { return path }

        let stepX = rect.width / CGFloat(waveformData.count - 1)
        let midY = rect.height / 2

        for (index, sample) in waveformData.enumerated() {
            let x = CGFloat(index) * stepX
            let y = midY - CGFloat(sample) * midY * 0.8

            if index == 0 {
                path.move(to: CGPoint(x: x, y: y))
            } else {
                path.addLine(to: CGPoint(x: x, y: y))
            }
        }

        return path
    }
}

