            Spacer()

            Button(action: {
                track.setWaveformType(.custom)
                track.clearCustomWaveform()
            }) {
                HStack {
                    Image(systemName: "xmark.circle.fill")
                        .font(.system(size: 16))
                    Text("Clear")
                        .font(.system(size: 14, weight: .medium))
                }
                .foregroundColor(.white)
            }
            .buttonStyle(LiquidglassButtonStyle())
        }
    }
}

