        phaseIncrement = frequency / Float(sampleRate)
    }

    func updateWaveformTable(_ newTable: [Float]) {
        waveformTable = newTable
    }

    func start() {
        isPlaying = true
    }

    func stop() {
        isPlaying = false
    }
}

